function formatState (state) {
    if (!state.id) {
        return state.text;
    }

    var baseUrl = "/img/idiomas";
    var $state = $(
        '<span><img class="img-flag" style="width: 28px !important; height: 28px !important;" /> <span></span></span>'
    );
    var imagem = $(state.element).attr('data-toggle').replace('.png', '');

    $state.find("span").text(state.text);
    $state.find("img").attr("src", baseUrl + "/" + imagem + ".png");

    return $state;
}

function format (state) {
    if (!state.id) {
        return state.text;
    }
    var baseUrl = "/img/idiomas";
    var imagem = $(state.element).attr('data-toggle').replace('.png', '');
    var $state = $(
        '<span><img src="' + baseUrl + '/' + imagem + '.png" class="img-flag" style="width: 28px !important; height: 28px !important;" /> ' + state.text + '</span>'
    );
    return $state;
}